import React, { useState } from "react";
import Axios from "axios";


export default function Login(props) {
  const [usernameState, setUsername] = useState("");
  const [passwordState, setPassword] = useState("");
  
  const onChangeUsername = (e) => {
    setUsername(e.target.value);
  };

  const onChangePassword = (e) => {
    setPassword(e.target.value);
  };

  const formSubmit = async (e) => {
    e.preventDefault();

    const postData = {
      username: usernameState,
      password: passwordState,
    };

    const res = await Axios.post("/api/login", postData);
    if (res.status === 200){
      props.setusernameState(usernameState);

      props.setLoginStatus("You are logged in");
    }
    else {
      props.setLoginStatus("An internal server error has been occur please try again")
    }
  };


  const divStyle = {
    display: "flex",
    flexDirection: "column",
  };

  const formStyle = {
    display: "flex",
    flexDirection: "column",
    padding: "5px 500px",
    order: 2,
    borderRadius: "30%",
  };

  return (
    <div
      // @ts-ignore
      style={divStyle}
    >
      <h1 style={{ alignSelf: "center", order: 1 }}>Login</h1>
      <form
        onSubmit={formSubmit}
        // @ts-ignore
        style={formStyle}
      >
        <input
          type="text"
          name="username"
          className="input"
          placeholder="Enter Username ..."
          style={{ paddingTop: "5px", paddingBottom: "5px"}}
          onChange={onChangeUsername}
        />
        <input
          type="password"
          name="password"
          className="input"
          placeholder="Enter Password ..."
          style={{ paddingTop: "5px", paddingBottom: "5px" }}
          onChange={onChangePassword}
        />
        <input
          type="submit"
          value="submit"
          className="btn"
          style={{ paddingTop: "5px" }}
        />
      </form>

    </div>
  );
}
