import React, { useState } from "react";
import Axios from "axios";
// @ts-ignore
import { Ip, Port } from "../config.json";

export default function Login() {
  const [usernameState, setUsername] = useState({ username: "" });
  const [passwordState, setPassword] = useState({ password: "" });

  const onChangeUsername = (e) => {
    setUsername({ username: e.target.value });
  };

  const formSubmit = async (e) => {
    e.preventDefault();

    const postData = {
      username: usernameState,
      password: passwordState,
    };

    console.log(postData);

    const res = await Axios.post("http://127.0.0.1:5000/api/login", postData);

    const data = res.data;
    console.log(data);
    //const data = JSON.parse(res.data);
    //console.log(data);
  };

  const onChangePassword = (e) => {
    setPassword({ password: e.target.value });
  };

  const divStyle = {
    display: "flex",
    flexDirection: "column",
  };

  const formStyle = {
    display: "flex",
    flexDirection: "column",
    padding: "5px 500px",
    order: 2,
    borderRadius: "30%",
  };

  const formdivStyle = {
    display: "flex",
    flexDirection: "column",
    order: 1,
    backgroundColor: "#C6C5BF",
    left: "400px",
    top:"100px",
    position:"absolute"
  };

  return (
    <div
      // @ts-ignore
      style={divStyle}
    >
      <div style={formdivStyle}>
        <h1 style={{ alignSelf: "center", order: 1 }}>Login</h1>
        <form
          onSubmit={formSubmit}
          // @ts-ignore
          style={formStyle}
        >
          <input
            type="text"
            name="username"
            placeholder="Enter Username ..."
            style={{ paddingTop: "5px", paddingBottom: "5px" ,}}
            onChange={onChangeUsername}
          />
          <input
            type="password"
            name="password"
            placeholder="Enter Password ..."
            style={{ paddingTop: "5px", paddingBottom: "5px" }}
            onChange={onChangePassword}
          />
          <input
            type="submit"
            value="submit"
            className="btn"
            style={{ paddingTop: "5px" }}
          />
        </form>
      </div>
    </div>
  );
}
