import React,{useState} from 'react'

export default function Loginstatus(props) {

    const [isPressed,setPressed] = useState(false);


    const statusStyle = () => {   
        console.log(props.status) 
        return {color: props.status === "You are logged in"? "#008":"#ff"};
    }

    const onClick = () => {
        setPressed(!isPressed);
    }

    return (
        <div style = {{display:"flex",flexDirection: "column",alignContent:"flexStart"}}>
            <p style = {statusStyle()}>{props.status}</p>
            <button onClick = {onClick}>Press here for more info</button>
        </div>
    )
}
