import React,{useState} from "react";
import axios from "axios"

export default function BuyMoney(props) {

    const [amount,setAmount] = useState(0);
    const [status,setStatus] = useState();

    const onChange = (e) =>{
        setAmount(e.target.value);
    }

    const onSubmit = async(e) => {
        e.preventDefault();
        if (!props.userData.username){
            setStatus("You need to be logged in");
        }
        else{ 
            if (!isNaN(amount) && amount >0){

                const postData = {
                    username: props.userData.username,
                    amount: amount
                };

                const res = await axios.post("/api/BuyMoney",postData);
                const data = res.data;
                
                if (data.transferd){
                    const new_amount = props.userData.money + parseInt(amount);

                    props.setUserdata({
                        ...props.userData,
                        money: new_amount
                    });
                }
            }
            else{
                setStatus("money cant be string or negative ");
            }
        }
    }


  return (
    <div>
      <h1>Enter amount</h1>
      <form onSubmit = {onSubmit}>
        <input
          type="text"
          name="amount"
          className="input"
          placeholder="Enter Amount ..."
          style={{ paddingTop: "5px", paddingBottom: "5px" }}
          onChange={onChange}
        />
        <input
          type="submit"
          value="Buy"
          className="btn"
          style={{ paddingTop: "5px" }}
        />
      </form>
      {status}
    </div>
  );
}
