import React, { useState } from "react";
import Axios from "axios";
import { Ip, Port } from "../config.json";

export default function Login() {
  const [usernameState, setUsername] = useState({ username: "" });
  const [passwordState, setPassword] = useState({ password: "" });

  const onChangeUsername = (e) => {
    setUsername({ username: e.target.value });
  };

  const formSubmit = async (e) => {
    e.preventDefault();

    const postData = {
      username: usernameState,
      password: passwordState,
    };

    console.log(postData);

    const res = await Axios.post("http://127.0.0.1:5000/api/login", postData);

    const data = res.data;
    console.log(data);
    //const data = JSON.parse(res.data);
    //console.log(data);
  };

  const onChangePassword = (e) => {
    setPassword({ password: e.target.value });
  };

  const divStyle = {
    display: "flex",
    flexDirection: "column"
  };

  return (
    <div style={divStyle}>
      <h1 style={{ alignSelf: "center" }}>Login</h1>
      <form 
      onSubmit={formSubmit}
      style = {{display: "flex"}}>
        <input
          type="text"
          name="username"
          placeholder="Enter Username ..."
          style={{ paddingTop: "5px", paddingBottom: "5px" }}
          onChange={onChangeUsername}
        />
        <input
          type="password"
          name="password"
          placeholder="Enter Password ..."
          style={{ paddingTop: "5px", paddingBottom: "5px" }}
          onChange={onChangePassword}
        />
        <input
          type="submit"
          value="submit"
          className="btn"
          style={{ paddingTop: "5px" }}
        />
      </form>
    </div>
  );
}
